entries.script:
console.log("--- MarpeScript Injected ---")

document.addEventListener("keydown", (e) => {
    if (
        e.target.nodeName === "INPUT" ||
        e.target.nodeName === "TEXTAREA" ||
        e.target.isContentEditable
    ) {
        return;
    }

    if (e.key === "ArrowUp") {
        console.log('pressed', e);
        e.preventDefault();
        e.stopPropagation();
    } else {
        console.log("pressed", e);
    }
});

window.printLocalStorage = () => {
    if (chrome.storage) {
        console.log("Local Storage");
        chrome.storage.local.get().then((r) => console.table(r));
    }
};
window.printLocalStorage();

window.printSharedStorage = () => {
    if (chrome.storage) {
        console.log("Shared Storage");
        chrome.storage.shared.get().then((r) => console.table(r));
    }
};
window.printSharedStorage();
